import './css/index.css';
import React, { useState, useEffect } from "react";
import './css/normalize.css';
import Header from './components/Header.js';
import Product from './components/Product.js';
import Footer from './components/Footer.js';
import ProductList from './components/Footer.js';

const getProductUrl = (categories, nextPage) => {
  return `https://api.appworks-school.tw/api/1.0/products/${categories}?paging=${nextPage}`;
}

function App() {
  const [categories, setCategories] = useState('all');
  const [search, setSearch] = useState();
  const [products, setProducts] = useState([]);
  const [nextPage, setNextPage] = useState(0);

  useEffect(() => {
    fetch(getProductUrl(categories, nextPage))
      .then((response) => response.json())
      .then((response) => {
        console.log(response);
        setProducts(response.data);
        setNextPage(response.next_paging);
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, [categories, nextPage]);//如果有變的話要重新拉資料！

  useEffect(() => {
    if (search ==="" || search === undefined){
      // errorMsg();
      alert('error');
    } else {
      fetch(
        `https://api.appworks-school.tw/api/1.0/products/search?keyword=${search}`
      )
        .then((response) => {
          if (!response.ok) {
            throw new Error("Network response was not ok");
          }
          return response.json();
        })
        .then((data) => {
          setProducts(data.data);
          setNextPage(data.next_paging);
        })
        .catch((error) => {
          // eslint-disable-next-line no-console
          console.error("Error:", error);
        });
    }
  }, [search])

  return (
    <div className='container'>      
      <Header
        categories={categories}
        search={search}
        onCategoryChange={setCategories}
        onSearch={setSearch}
      />
      <ProductList products={products} />
      {/* <Product /> */}
      <Footer />  
    </div>
  );
}

export default App;
