import React, { useState, useEffect } from "react";

const urlParams = new URLSearchParams(window.location.search);
const getProductID = urlParams.get('id')
const getProductUrl = (categories, nextPage) => {
  return `https://api.appworks-school.tw/api/1.0/products/${categories}?paging=${nextPage}`;
}

function ProductList() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState('all');
  const [nextPage, setNextPage] = useState(0);


  useEffect(() => {
    fetch(getProductUrl(categories, nextPage))
      .then((response) => response.json())
      .then((response) => {
        console.log(response);
        setProducts(response.data);
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, [categories, nextPage]);//如果有變的話要重新拉資料！


  return (
    <section className="contents">
      <div className="items">
        {products.map((data, index) => (
          <div className="item1">
          <a className="dress-img">
            <img className="contents-img" src={data.main_image}/>
          </a>
          <div className="color-pads"> 
            {data.colors.map((color, index) => (
              <div
                key={index}
                className="color-pad"
                style={{ backgroundColor: "#" + color.code }}></div>
            ))}
          </div>
          <div className="title">{data.title}</div>
          <div className="price">TWD.{data.price}</div>
        </div>
          ))}
      </div>
    </section>
  ) 
}


export default ProductList;
