import React, { useState, useEffect } from "react";

const urlParams = new URLSearchParams(window.location.search);

const categories = urlParams.get('id')
let nextPage = 0;
const urlCategories = urlParams.get("category") || "all";
const productListUrl =`https://api.appworks-school.tw/api/1.0/products/${categories}?paging=${nextPage}`;


function ProductList() {
  const categories = urlCategories;
  const [products, setProducts] = useState();
  useEffect(() => {
    fetch(productListUrl)
      .then((response) => response.json())
      .then((response) => {
        console.log(response);
        setProducts(response);
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, []);
  return (
    <section className="contents">
      <div className="items">
        {products.map((data, index1) => (
          <div className="item1" key={index1}>
          <a className="dress-img">
            <img className="contents-img" src={data.main_image} alt="productimage"/>
          </a>
          <div className="color-pads"> 
            {data.colors.map((color, index) => (
              <div
                key={index}
                className="color-pad"
                style={{ backgroundColor: "#" + color.code }}></div>
            ))}
          </div>
          <div className="title">{data.title}</div>
          <div className="price">TWD.{data.price}</div>
        </div>
          ))}
      </div>
    </section>
  ) 
}


export default ProductList;
