/* eslint-disable jsx-a11y/anchor-is-valid */
import Logo from "../img/logo.png";
import Search from "../img/search.png";
import Cart from "../img/cart.png";
import Member from "../img/member.png";
import React, { useState, useEffect } from "react";


// const userInput = document.querySelector(".input");
// const mobileSearchBtnOutside=document.querySelector(".mobile-search-btn-outside");
// const mobileSearch = document.querySelector(".mobile-search");
// const mobileInput = document.querySelector(".mobile-input");
// const mobileSearchBtnInside = document.querySelector(".mobile-search-btn-inside"); 



function Header({ categories, search, onCategoryChange, onSearch }) {
  // const [userInput, setUserInput] = useState();

  function handleKeypress(e) {
    // loading = true;
    if (e.key === "Enter") {
      e.preventDefault();
      const input = e.target.value;
      onSearch(input);
      // const newUrl = `${location.protocol}//${location.host}${location.pathname}?search=${input}`;
      // window.history.replaceState({}, "", newUrl);
      // getKeywordData(input);
      // e.target.placeholder = "";
    }
  }

  function errorMsg() {
    const cantFind = document.querySelector(".contents");
    const msg = `<div class="error"><h3>Oooops!<br>您搜尋的商品不存在，請重新搜尋</h3></div>`;
    cantFind.style.textAlign = "center";
    cantFind.innerHTML = msg;
  }

  return (
    <header>
      <div className="webhead">
        <div className="navbar">
          <div className="logo-category">
            <div className="logo">
              <a href="./?category=all">
                <img src={Logo} alt="logo" />
              </a>
            </div>

            <div className="category">
              <a href="./?category=women" className="women">
                女裝
              </a>
              <a href="./?category=men" className="men">
                男裝
              </a>
              <a href="./?category=accessories" className="accessories">
                配件
              </a>
            </div>

            <div className="mobile-search-btn-outside">
              <a href="./?search">
                <img src={Search} alt="search" />
              </a>
            </div>

            <div className="mobile-search" style={{ display: "none" }}>
              <div className="mobile-search-box">
                <div className="mobile-search-area">
                  <input
                    className="mobile-input"
                    type="text"
                    placeholder="Search"
                    onKeyUp={handleKeypress}
                  />
                  <a href="./?category=all">
                    <img
                      className="mobile-search-btn-inside"
                      src={Search}
                      alt="search"
                    />
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="search-cart-member">
          <div className="search">
            <form className="search-form">
              <input
                className="input"
                type="text"
                placeholder="Search"
                value=""
              />
              <img className="search-btn" src={Search} alt="search" />
            </form>
          </div>

          <div className="cart-member">
            <div className="cart">
              <a>
                <div>
                  <img src={Cart} alt="cart" />
                </div>
              </a>
            </div>
            <div className="cart-number">
              <p>1</p>
            </div>
          </div>

          <div className="member">
            <a>
              <img src={Member} alt="member-icon" />
            </a>
          </div>
        </div>
      </div>
      <div className="black-line"></div>
    </header>
  );
}

export default Header;
